//IMPORT THE LIBRARY THROUGH THE "script.js" FILE
import { calculator } from "../script.js"

//CALLING THE HELP FUNCTION TO SHOW ALL USE OPTIONS ON BROWSER CONSOLE
console.log(calculator.help())

let inputBit = document.getElementById('inputBit')
inputBit.addEventListener('keyup',callConverterFunction,false)

function callConverterFunction() {
    let htmlReturn = document.getElementById('converted')
    let convertedValue = calculator.convertBitToByte(inputBit.value)
    htmlReturn.innerHTML = convertedValue
}