//IMPORT THE LIBRARY THROUGH THE "script.js" FILE
import { calculator } from "../script.js"
//CALLING THE HELP FUNCTION TO SHOW ALL USE OPTIONS ON BROWSER CONSOLE
console.log(calculator.help())

//SIMULATING AN INPUT VALUE TO CONVERT FROM BITS TO BYTES
let inputInBits = 16

//CREATING A FUNCTION TO CONVERT THE INPUT VALUE TO BYTES AND RETURN A MESSAGE WITH THE OUTPUT VALUE, ALREADY FORMATED
function example(data) {
    let message = `Valor de entrada (Bits): ${data}\nValor de sa√≠da (Bytes): `
    let outputInBytes = calculator.convertBitToByte(data)
    return message + outputInBytes + `\n\n`
}

//THROWING THE MESSAGE ON THE CONSOLE
console.log(example(inputInBits))